import React, { Fragment } from 'react';
import Header from './Header';
import Footer from './Footer';

const Layout = (props) => {
  const { children } = props;
  return (
    <Fragment>
      <div id="wrap">
        <Header />
        { children }
        <Footer />
      </div>
      <style jsx global>
        {`
          *{
            padding: 0;
            margin: 0;
            background-color: black;
          }
        `}
      </style>
    </Fragment>
  );
};

export default Layout;
