import React, { Fragment } from 'react';
import Header from './Header';
import Footer from './Footer';

const Layout = (props) => {
  const { children } = props;
  return (
    <Fragment>
      <div id="wrap">
        <Header />
        { children }
      </div>
      <style jsx>
        {`
          *{
            padding: 0;
            margin: 0;
          }
        `}
      </style>
    </Fragment>
  );
};

export default Layout;
